package democode;

public class Pyramid1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//int k=1;
		for(int i=0;i<5;i++)
		{
			
			for(int j=5-i;j>=1;j--)       
			{
				System.out.print(j);
				System.out.print("\t");
				//k++;
			}
				System.out.println("");
		}

	}

}
